INSERT INTO db_script_string (entry, content_default, sound, type, language, emote, comment) VALUES
(2000001497,'Glorious, indeed, Maltrake. It looks as if my plan worked perfectly. Wouldn\'t you say so, mortal?',0,0,0,0,NULL),
(2000001498,'%s lets loose a reptilian laugh... at least you think it\'s a laugh.',0,2,0,0,NULL),
(2000001499,'You would not have helped the legion of Blackrock had we just asked for your assistance, now would you?',0,0,0,0,NULL),
(2000001500,'What will we do with them, master? They are of no use to us anymore.',0,0,0,1,NULL),
(2000001501,'SILENCE FOOL!',0,0,0,0,NULL),
(2000001502,'They will live of only to see the fruits of their labor: the destruction and chaos that will surely ensue as the legion of Blackrock invade the gorge.',0,0,0,0,NULL),
(2000001503,'I leave you now, mortals. Alive and with these trinkets. Maltrake! Present them with the trinkets!',0,0,0,0,NULL),
(2000001504,'Right away, master!',0,0,0,0,NULL),
(2000001505,'%s begins to flap his massive wings faster. He is preparing for flight.',0,2,0,0,NULL),
(2000001506,'Oh yes, the molt - do not lose it. There are those of my kin in the Burning Steppes that would craft items that only the molt of the black dragonflight could fortify.',0,0,0,0,NULL),
(2000001507,'The legion of Blackrock comes, dwarflings! We shall scorch the earth and set fire to the heavens. None shall survive...',0,1,0,0,NULL);
