INSERT INTO db_script_string (entry, content_default, sound, type, language, emote, comment) VALUES
(2000001524,'My brothers and sisters. Listen to me. We must speak of the survival of our people. We have narrowly survived one calamity, but I fear the next shall destroy us.',0,0,0,1,NULL),
(2000001525,'Can you not see what is happening? Open your eyes. It is laid bare for all to see. What is this new alliance? Why have we betrayed our allies of old, only to take up with the enemies of all that live?',0,0,0,1,NULL),
(2000001526,'No, you\'re wrong! We were the ones betrayed!',0,0,0,22,NULL),
(2000001527,'When have the dwarves ever been our friends?',0,0,0,11,NULL),
(2000001528,'The humans abandoned us in our darkest hour! Their prince himself led the assault on our lands.',0,0,0,25,NULL),
(2000001529,'You fool! The prince\'s minions were the very Forsaken we have now allied ourselves with, and now he has betrayed us!',0,0,0,5,NULL),
(2000001530,'The Ranger-General of Silvermoon will be a loyal ally!',0,0,0,1,NULL),
(2000001531,'You speak of her as friend? You are a fool. This "Dark Lady" is not Sylvanas.',0,0,0,1,NULL),
(2000001532,'Blood will be true. We can trust in none but our own!',0,0,0,25,NULL),
(2000001533,'Blood? And these orcs, these fair weather friends? They will aid us?',0,0,0,11,NULL),
(2000001534,'A great blight lies across our land! It grows every day. Our need for magic weakens us, and will bring us to destruction, just as it has before!',0,0,0,25,NULL),
(2000001535,'How are we to live without magic?',0,0,0,5,NULL),
(2000001536,'Look at the wonders of this city! This is magic.',0,0,0,5,NULL),
(2000001537,'But at what cost? You\'ve all been blinded. We have all been blinded.',0,0,0,1,NULL),
(2000001538,'You call us traitors?',0,0,0,25,NULL),
(2000001539,'The magisters are the traitors! They have sealed our doom.',0,0,0,25,NULL),
(2000001540,'Idealistic blather. Go back to your forests!',0,0,0,3,NULL),
(2000001541,'You would have us live like the savages!',0,0,0,5,NULL),
(2000001542,'No, not savagery! Conscience. The excesses of our people will destroy us, and all that we have struggled to build.',0,0,0,1,NULL),
(2000001543,'We have not struggled so hard to survive, only to bring about the true destruction.',0,0,0,1,NULL),
(2000001544,'We stand on the brink of destruction. We must reconsider this rash course of action.',0,0,0,5,NULL),
(2000001545,'If we speak as one, we cannot be ignored!',0,0,0,1,NULL),
(2000001546,'Lend us your voices! Now is the time we must act to save our people.',0,0,0,1,NULL),
(2000001547,'Lor\'themar must hear us! We will not be silenced!',0,0,0,5,NULL),
(2000001548,'That should take care of that. Summon me if you have further need of my services.',0,0,0,11,NULL),
(2000001549,'We will repay the betrayal of the Alliance! We shall never forget, and never forgive!',0,0,0,21,NULL),
(2000001550,'How can you say our lives are not better now? How can you deny the betterment of our lives?',0,0,0,21,NULL),
(2000001551,'Lor\'themar has remade Silvermoon into something we may truly be proud of.',0,0,0,21,NULL),
(2000001552,'Our new allies will bring us back to our former glory. These bonds will be true, and we will not be betrayed again!',0,0,0,21,NULL);
