INSERT INTO db_script_string (entry, content_default, sound, type, language, emote, comment) VALUES 
(2000001555,'Mortals, here?  What is the meaning of this, pathetic Broken!',0,1,0,0,NULL),
(2000001556,'Have no fear, $r.  Just play along.',0,4,0,0,NULL),
(2000001557,'A mere nuisance, I assure you!  Tell the Master his prisoner will not escape while Akama and his Deathsworn watch over her.',0,1,0,0,NULL),
(2000001558,'You\'d do well not to toy with me, Akama.  Illidan has given me specific orders to keep a close watch on the Warden.  If I find out you\'re hiding anything from me, I will crush you with my own hands!',0,0,0,0,NULL),
(2000001559,'Forgive my harsh methods, but the Betrayer cannot learn of the truth.  My secret must be kept at all costs!',0,0,0,0,NULL),
(2000001560,'If we truly desire the same thing, Akama, then release me!  If Illidan is to die, it shall be by my hand!',0,0,0,0,NULL),
(2000001561,'In due time, Maiev.  I\'ve spent years preparing to make my move - I can\'t afford to put my plans in peril by tipping my hand too soon.',0,0,0,0,NULL),
(2000001562,'Curse you, Akama!  I am not a pawn in your game... my will is my own.  When I unleash my wrath upon Illidan it will have nothing to do with your foolish scheme!',0,0,0,0,NULL);
