INSERT INTO db_script_string (entry, content_default, sound, type, language, emote, comment) VALUES
(2000001733,'What about the earth spirits?', 0, 0, 0, 0, NULL),
(2000001734,'%s chuckles softly.', 0, 2, 0, 11, NULL),
(2000001735,'The earth spirits are very much present in this chamber, my child. After all, we are underground, are we not?', 0, 0, 0, 6, NULL),
(2000001736,'They are stoic, the spirits of the earth. Through them, the shaman learns to endure the hardships of life.', 0, 0, 0, 1, NULL),
(2000001737,'The rocks are here when we first enter this world, and they will remain long after we pass.', 0, 0, 0, 1, NULL),
(2000001738,'Stand... there is more to discuss.', 0, 0, 0, 1, NULL),
(2000001739,'Stop here a moment, my children, and close your eyes. Can you feel the air currents?', 0, 0, 0, 1, NULL),
(2000001740,'The two students close their eyes and bow their heads.', 0, 2, 0, 0, NULL),
(2000001741,'Yes, teacher.', 0, 0, 0, 273, NULL),
(2000001742,'The spirits of the air can be found even here, underground. They dislike being surrounded, constricted by the earth... their precious freedom is limited here.', 0, 0, 0, 1, NULL),
(2000001743,'They are flighty, these spirits, and easily distracted... but they will share their secrets with those who know how to listen.', 0, 0, 0, 1, NULL),
(2000001744,'%s smiles to himself as he watches the students fidget in their concentration.', 0, 2, 0, 0, NULL),
(2000001745,'Come. There is more to study than the secrets on the wind.', 0, 0, 0, 1, NULL),
(2000001746,'Can you feel the warmth here, my children?', 0, 0, 0, 6, NULL),
(2000001747,'The students nod slowly, eyes rapt on their teacher.', 0, 2, 0, 0, NULL),
(2000001748,'Focus upon that warmth as I speak...', 0, 0, 0, 1, NULL),
(2000001749,'The strength of fire lies in change. Its strength is considerable, but ever-shifting... unpredictable.', 0, 0, 0, 1, NULL),
(2000001750,'This is extended to the spirits themselves. They are often quick to anger; it is important thet you learn to listen without falling prey to their tempers.', 0, 0, 0, 1, NULL),
(2000001751,'That is enough for now. Come.', 0, 0, 0, 1, NULL),
(2000001752,'The water here is peaceful, is it not? Listening to the trickle from the ceiling can aid you in finding its true voice...', 0, 0, 0, 1, NULL),
(2000001753,'The water spirits are perhaps the most outspoken. They are generous and understanding. Indeed, if you are having trouble hearing the voices, it is with water that you should begin.', 0, 0, 0, 1, NULL),
(2000001754,'Close your eyes, my children, and listen to the water within these halls.', 0, 0, 0, 1, NULL),
(2000001755,'Both students close their eyes, straining to hear the voices.', 0, 2, 0, 0, NULL),
(2000001756,'%s nods to himself and clears his throat.', 0, 2, 0, 273, NULL),
(2000001757,'Stand, my children... that\'s enough for now.', 0, 0, 0, 0, NULL);
